base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Fernando Tellado";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1366:"<p>WordPress 4.4 Beta 2 ya está disponible para su descarga y prueba. Es aún un software en fase de desarrollo así que no te recomendamos que lo uses en un sitio en producción. Para probar la beta puedes usar el plugin <a href="https://wordpress.org/extend/plugins/wordpress-beta-tester/">WordPress Beta Tester</a> (on el que puedes incluso probar versiones de desarrollo temprano). Si lo prefieres también puedes <a href="https://wordpress.org/wordpress-4.4-beta2.zip">descargar aquí la beta</a> (zip).</p>\n<p>Para más información sobre las novedades de la versión 4.4 echa un vistazo a <a href="https://es.wordpress.org/2015/10/22/wordpress-4-4-beta-1/">esta entrada en el blog de la beta 1</a>.</p>\n<p><strong>Si encuentras algún fallo</strong> puedes enviarlo al <a href="https://wordpress.org/support/forum/alphabeta">área Alpha/Beta</a> de los foros de soporte. O, si te animas a escribir un informe de fallos, <a href="https://core.trac.wordpress.org/">manda uno al trac de WordPress</a>. Aquí podrás encontrar una <a href="https://core.trac.wordpress.org/tickets/major">lista de fallos conocidos</a> y <a href="https://core.trac.wordpress.org/query?status=closed&amp;group=component&amp;milestone=4.4">todo lo que hemos arreglado</a>.</p>\n<p><em>Cuatro-cuatro beta dos<br />\nOtra semana de progreso<br />\n¡REST API vive!</em></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:62:"https://es.wordpress.org/2015/10/29/wordpress-4-4-beta-2/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:8;a:6:{s:4:"data";s:48:"\n		\n		\n		\n		\n		\n				\n		\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:20:"WordPress 4.4 beta 1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:57:"https://es.wordpress.org/2015/10/22/wordpress-4-4-beta-1/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:66:"https://es.wordpress.org/2015/10/22/wordpress-4-4-beta-1/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 22 Oct 2015 18:18:17 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:3:{i:0;a:5:{s:4:"data";s:15:"Actualizaciones";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:3:"4.4";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:8:"REST API";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:31:"https://es.wordpress.org/?p=500";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:354:"¡WordPress 4.4 Beta 1 ya está disponible! Este software está aún en fase de desarrollo, así que no te recomendamos que lo uses en un sitio en producción. Anímate a crear un sitio de pruebas para probar la nueva versión. Para probar WordPress 4.4 puedes instalar el plugin WordPress Beta Tester  (con el que instalar incluso versiones [&#8230;]";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Fernando Tellado";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4543:"<p>¡WordPress 4.4 Beta 1 ya está disponible!</p>\n<p><strong>Este software está aún en fase de desarrollo,</strong> así que no te recomendamos que lo uses en un sitio en producción. Anímate a crear un sitio de pruebas para probar la nueva versión. Para probar WordPress 4.4 puedes instalar el plugin <a href="https://wordpress.org/plugins/wordpress-beta-tester/" target="_blank">WordPress Beta Tester</a>  (con el que instalar incluso versiones en desarrollo temprano). También puedes <a href="https://wordpress.org/wordpress-4.4-beta1.zip" target="_blank">descargar aquí la beta</a> (zip).</p>\n<p>WordPress 4.4 está programada para lanzarse el <a href="https://make.wordpress.org/core/version-4-4-project-schedule/" target="_blank">8 de diciembre</a>, pero antes tenemos que hacer pruebas para comprobar que todo funciona, e incluye:</p>\n<ul>\n<li><strong>Twenty Sixteen </strong>— El <a href="https://make.wordpress.org/core/2015/08/25/introducing-twenty-sixteen/">nuevo</a> tema por defecto de WordPress.</li>\n<li><strong>Imágenes adaptables </strong>— WordPress mostrará automáticamente la <a href="https://make.wordpress.org/core/2015/09/30/responsive-images-merge-proposal/">imagen más apropiada</a> a los usuarios dependiendo de los distintos tamaños de pantalla, tamaño del dispositivo y resoluciones de pantalla.</li>\n<li><strong>Incrustados </strong>— WordPress puede ahora incrustar contenido enriquecido de casi cualquier web usando el estándar oEmbed &#8211; no solo YouTube, Flickr, Twitter y estos. Ahora incluso puedes incrustar <a href="https://make.wordpress.org/core/2015/09/30/feature-plugin-merge-proposal-oembed/">vistas previas de entradas</a> de otros sitios WordPress simplemente pegando la URL en una nueva línea del editor.</li>\n</ul>\n<p>Hay otro montón de cambios para desarrolladores con los que también puedes jugar:</p>\n<ul>\n<li><strong>REST API (fase 1) </strong>— La infraestructura base del <a href="https://wordpress.org/plugins/rest-api/">plugin</a>  WordPress REST API se ha incluido en<a href="https://make.wordpress.org/core/2015/09/21/wp-rest-api-merge-proposal/"> WordPress 4.4</a>. Los autores de plugins pueden aprovechar esta funcionalidad añadiendo funciones personalizadas.</li>\n<li><strong>Metadatos en términos </strong>— Los metadatos de la taxonomía de términos ahora estarán <a href="https://make.wordpress.org/core/2015/09/04/taxonomy-term-metadata-proposal/">disponibles</a> en WordPress 4.4. Si ya estás usando un plugin que incorpore metadatos de términos deberías <a href="https://make.wordpress.org/core/2015/09/22/preparing-your-plugins-and-your-client-sites-for-termmeta/">leer esta entrada</a> para saber como prepararte. Además, la clase <code>WP_Term</code> mejora la cache cuando trabaja con términos. (<a href="https://core.trac.wordpress.org/ticket/14162">#14162</a>)</li>\n<li><strong>Mejoras en la salida <code>&lt;title&gt;</code> </strong> — <code>wp_title()</code> queda desde ahora obsoleta; WordPress puede <a href="https://make.wordpress.org/core/2015/10/20/document-title-in-4-4/">gestionar la salida</a> del título del documento automáticamente.</li>\n<li><strong>Comentarios </strong>— Las peticiones de los comentarios a partir de ahora se separan para mejorar el rendimiento. Además, la clase <code>WP_Comment</code> mejora la cache e introduce el tipeo fuerte. (<a href="https://core.trac.wordpress.org/ticket/8071">#8071</a>, <a href="https://core.trac.wordpress.org/ticket/32619">#32619</a>)</li>\n</ul>\n<p>Si quieres un mayor detalle acerca de los principales cambios realizados en 4.4 <a href="https://make.wordpress.org/core/tag/4-4/" target="_blank">echa un vistazo a todas las entradas con la tag 4.4</a> del blog de desarrollo, o revisa <a href="https://core.trac.wordpress.org/query?status=closed&amp;group=component&amp;milestone=4.4">la lista de todo</a> lo que cambia.</p>\n<p><strong>Si encuentras algún fallo</strong> puedes enviarlo al <a href="https://wordpress.org/support/forum/alphabeta">área Alpha/Beta</a> de los foros de soporte. O, si te animas a escribir un informe de fallos, <a href="https://core.trac.wordpress.org/">manda uno al tras de WordPress</a>. Aquí tienes también una <a href="https://core.trac.wordpress.org/tickets/major" target="_blank">lista de fallos encontrados</a>.</p>\n<p>¡Felices pruebas!</p>\n<p><em>Muchos pequeños cambios</em><br />\n<em>Algunas nuevas características  innovadoras<br />\nDiversión para todos</em></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:62:"https://es.wordpress.org/2015/10/22/wordpress-4-4-beta-1/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"4";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:48:"\n		\n		\n		\n		\n		\n				\n		\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:59:"WordPress 4.3.1 Actualización de mantenimiento y seguridad";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:95:"https://es.wordpress.org/2015/09/15/wordpress-4-3-1-actualizacion-de-mantenimiento-y-seguridad/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:104:"https://es.wordpress.org/2015/09/15/wordpress-4-3-1-actualizacion-de-mantenimiento-y-seguridad/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 15 Sep 2015 18:49:31 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:3:{i:0;a:5:{s:4:"data";s:15:"Actualizaciones";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:9:"Seguridad";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:3:"4.3";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:31:"https://es.wordpress.org/?p=473";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:408:"WordPress 4.3.1 está ya disponible. Esta es una actualización de seguridad para todas las versiones anteriores así que es importante que actualices tus sitios de inmediato. Esta versión soluciona tres problemas, incluidas dos vulnerabilidades de scripts cruzados (XSS) y un escalado potencial de privilegios Las versiones de WordPress 4.3 y anteriores son vulnerables a una vulnerabilidad XSS [&#8230;]";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Fernando Tellado";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4194:"<div class="storycontent">\n<p>WordPress 4.3.1 está ya disponible. Esta es una <strong>actualización de seguridad</strong> para todas las versiones anteriores así que es importante que actualices tus sitios de inmediato.</p>\n<p>Esta versión soluciona tres problemas, incluidas dos vulnerabilidades de scripts cruzados (XSS) y un escalado potencial de privilegios</p>\n<ul>\n<li>Las versiones de WordPress 4.3 y anteriores son vulnerables a una vulnerabilidad XSS al procesar tags de shortcode (CVE-2015-5714). Avisado por Shahar Tal y Netanel Rubin de <a href="http://checkpoint.com/">Check Point</a>.</li>\n<li>Se ha encontrado otra vulnerabilidad XSS en la tabla de la lista de usuarios. Avisado por Ben Bidner del Equipo de seguridad de WordPress.</li>\n<li>Por último, en ciertos casos, usuarios sin los permisos adecuados podrían publicar entradas privadas y hacerlas fijas (CVE-2015-5715). Avisado por Shahar Tal y Netanel Rubin de <a href="http://checkpoint.com/">Check Point</a>.</li>\n</ul>\n<p>Todo nuestro agradecimiento a los que han hecho una <a href="https://make.wordpress.org/core/handbook/testing/reporting-security-vulnerabilities/">divulgación responsable</a> de problemas de seguridad.</p>\n<p>WordPress 4.3.1 también arregla otros 26 fallos. Para más información revisa las <a href="https://codex.wordpress.org/Version_4.3.1">notas de la versión</a> o consulta la <a href="https://core.trac.wordpress.org/log/branches/4.3/?rev=34199&amp;stop_rev=33647">lista de cambios</a>.</p>\n<p><a href="https://wordpress.org/download/">Descarga WordPress 4.3.1</a> o pásate por Escritorio → Actualizaciones y haz clic en  “Actualizar ahora.” Los sitios que tengan activas las actualizaciones automáticas desatendidas ya están empezando a actualizarse a WordPress 4.3.1.</p>\n</div>\n<p>Gracias a todos los que han contribuido a la versión 4.3.1:</p>\n<p><a href="https://profiles.wordpress.org/adamsilverstein">Adam Silverstein</a>, <a href="https://profiles.wordpress.org/afercia">Andrea Fercia</a>, <a href="https://profiles.wordpress.org/azaozz">Andrew Ozz</a>, <a href="https://profiles.wordpress.org/boonebgorges">Boone Gorges</a>, <a href="https://profiles.wordpress.org/kraftbj">Brandon Kraft</a>, <a href="https://profiles.wordpress.org/chriscct7">chriscct7</a>, <a href="https://profiles.wordpress.org/extendwings">Daisuke Takahashi</a>, <a href="https://profiles.wordpress.org/dd32">Dion Hulse</a>, <a href="https://profiles.wordpress.org/ocean90">Dominik Schilling</a>, <a href="https://profiles.wordpress.org/DrewAPicture">Drew Jaynes</a>, <a href="https://profiles.wordpress.org/dustinbolton">dustinbolton</a>, <a href="https://profiles.wordpress.org/pento">Gary Pendergast</a>, <a href="https://profiles.wordpress.org/hauvong">hauvong</a>, <a href="https://profiles.wordpress.org/macmanx">James Huff</a>, <a href="https://profiles.wordpress.org/jeremyfelt">Jeremy Felt</a>, <a href="https://profiles.wordpress.org/jobst">jobst</a>, <a href="https://profiles.wordpress.org/tyxla">Marin Atanasov</a>, <a href="https://profiles.wordpress.org/celloexpressions">Nick Halsey</a>, <a href="https://profiles.wordpress.org/nikeo">nikeo</a>, <a href="https://profiles.wordpress.org/nbachiyski">Nikolay Bachiyski</a>, <a href="https://profiles.wordpress.org/swissspidy">Pascal Birchler</a>, <a href="https://profiles.wordpress.org/figureone">Paul Ryan</a>, <a href="https://profiles.wordpress.org/peterwilsoncc">Peter Wilson</a>, <a href="https://profiles.wordpress.org/miqrogroove">Robert Chapin</a>, <a href="https://profiles.wordpress.org/otto42">Samuel Wood</a>, <a href="https://profiles.wordpress.org/wonderboymusic">Scott Taylor</a>, <a href="https://profiles.wordpress.org/SergeyBiryukov">Sergey Biryukov</a>, <a href="https://profiles.wordpress.org/tmatsuur">tmatsuur</a>, <a href="https://profiles.wordpress.org/liljimmi">Tracy Levesque</a>, <a href="https://profiles.wordpress.org/umeshnevase">Umesh Nevase</a>, <a href="https://profiles.wordpress.org/vortfu">vortfu</a>, <a href="https://profiles.wordpress.org/welcher">welcher</a>, <a href="https://profiles.wordpress.org/westonruter">Weston Ruter</a></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:100:"https://es.wordpress.org/2015/09/15/wordpress-4-3-1-actualizacion-de-mantenimiento-y-seguridad/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"6";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:30:"https://es.wordpress.org/feed/";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:9:{s:6:"server";s:5:"nginx";s:4:"date";s:29:"Thu, 25 Feb 2016 14:57:18 GMT";s:12:"content-type";s:34:"application/rss+xml; charset=UTF-8";s:10:"connection";s:5:"close";s:6:"x-olaf";s:3:"⛄";s:13:"last-modified";s:29:"Fri, 12 Feb 2016 12:44:15 GMT";s:4:"link";s:61:"<https://es.wordpress.org/wp-json/>; rel="https://api.w.org/"";s:15:"x-frame-options";s:10:"SAMEORIGIN";s:4:"x-nc";s:11:"HIT lax 