--
-- Estructura de tabla para la tabla `wp_posts`
--

CREATE TABLE IF NOT EXISTS `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0'
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Volcado de datos para la tabla `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-09-11 19:01:17', '2015-09-11 19:01:17', 'Solo cuerpo de iván', 'Juan Pablo Castro Soto', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2016-02-24 20:28:32', '2016-02-24 20:28:32', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2015-09-11 19:01:17', '2015-09-11 19:01:17', 'Esto es una página de ejemplo. Es diferente a una entrada porque permanece fija en un lugar y se mostrará en la navegación de tu sitio (en la mayoría de los temas). La mayoría de la gente empieza con una página de Acerca de, que les presenta a los potenciales visitantes del sitio. Podría ser algo como esto:\n\n<blockquote>¡Hola! Soy mensajero por el día, aspirante a actor por la noche, y este es mi blog. Vivo en Madrid, tengo un perrazo llamado Duque y me gustan las piñas coladas (y que me pille un chaparrón)</blockquote>\n\n...o algo así:\n\n<blockquote>La empresa XYZ se fundó en 1971 y ha estado ofreciendo "cosas" de calidad al público desde entonces. Situada en Madrid, XYZ emplea a más de 2.000 personas y hace todo tipo de cosas sorprendentes para la comunidad de Madrid.</blockquote>\n\nSi eres nuevo en WordPress deberías ir a <a href="http://localhost/wordpress/wp-admin/">tu escritorio</a> para borrar esta página y crear páginas nuevas con tu propio contenido. ¡Pásalo bien!', 'Página de ejemplo', '', 'publish', 'open', 'open', '', 'pagina-ejemplo', '', '', '2015-09-11 19:01:17', '2015-09-11 19:01:17', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(4, 1, '2016-01-12 19:44:23', '2016-01-12 19:44:23', '', 'img_header', '', 'inherit', 'open', 'closed', '', 'img_header', '', '', '2016-01-12 19:44:23', '2016-01-12 19:44:23', '', 0, 'http://localhost/wordpress/wp-content/uploads/2016/01/img_header.png', 0, 'attachment', 'image/png', 0),
(5, 1, '2016-01-12 19:45:17', '2016-01-12 19:45:17', 'http://localhost/wordpress/wp-content/uploads/2016/01/cropped-img_header.png', 'cropped-img_header.png', '', 'inherit', 'open', 'closed', '', 'cropped-img_header-png', '', '', '2016-01-12 19:45:17', '2016-01-12 19:45:17', '', 0, 'http://localhost/wordpress/wp-content/uploads/2016/01/cropped-img_header.png', 0, 'attachment', 'image/png', 0),
(6, 1, '2016-01-12 19:45:35', '2016-01-12 19:45:35', 'http://localhost/wordpress/wp-content/uploads/2016/01/cropped-img_header1.png', 'cropped-img_header1.png', '', 'inherit', 'open', 'closed', '', 'cropped-img_header1-png', '', '', '2016-02-24 19:33:29', '2016-02-24 19:33:29', '', 9, 'http://localhost/wordpress/wp-content/uploads/2016/01/cropped-img_header1.png', 0, 'attachment', 'image/png', 0),
(8, 1, '2016-01-12 19:54:53', '2016-01-12 19:54:53', 'Esto es una página de ejemplo. Es diferente a una entrada porque permanece fija en un lugar y se mostrará en la navegación de tu sitio (en la mayoría de los temas). La mayoría de la gente empieza con una página de Acerca de, que les presenta a los potenciales visitantes del sitio. Podría ser algo como esto:\n<blockquote>¡Hola! Soy mensajero por el día, aspirante a actor por la noche, y este es mi blog. Vivo en Madrid, tengo un perrazo llamado Duque y me gustan las piñas coladas (y que me pille un chaparrón)</blockquote>\n...o algo así:\n<blockquote>La empresa XYZ se fundó en 1971 y ha estado ofreciendo "cosas" de calidad al público desde entonces. Situada en Madrid, XYZ emplea a más de 2.000 personas y hace todo tipo de cosas sorprendentes para la comunidad de Madrid.</blockquote>\nSi eres nuevo en WordPress deberías ir a <a href="http://localhost/wordpress/wp-admin/">tu escritorio</a> para borrar esta página y crear páginas nuevas con tu propio contenido. ¡Pásalo bien!', 'Página de ejemplo', '', 'inherit', 'closed', 'closed', '', '2-autosave-v1', '', '', '2016-01-12 19:54:53', '2016-01-12 19:54:53', '', 2, 'http://localhost/wordpress/2016/01/12/2-autosave-v1/', 0, 'revision', '', 0),
(9, 1, '2016-02-24 13:45:02', '2016-02-24 13:45:02', '<div>\r\n\r\nAyer Arturo Vidal se reencontró la Juventus, su antiguo club, en el empate 2-2 del Bayern Munich en Italia por la ida de los octavos de final de la Champions League. El día después de aquel partido, el volante chileno utilizó su cuenta de Twitter para mandar un mensaje a todos sus seguidores, tanto en italiano como en español. "Un dia de muchas emociones...gracias a todos los Juventinos por tanto cariño...nos vemos el 16 en Munich!!!", escribió en la red social.\r\n\r\n</div>\r\n<a href="http://localhost/wordpress/wp-content/uploads/2016/01/cropped-img_header1.png"><img class="alignnone size-medium wp-image-6" src="http://localhost/wordpress/wp-content/uploads/2016/01/cropped-img_header1-300x300.png" alt="cropped-img_header1.png" width="300" height="300" /></a>\r\n\r\nfin de la noticia', 'Champions al día: La favorable estadística del rival del City de Pellegrini', 'solo un extracto', 'publish', 'open', 'open', '', 'informacion-numero-1', '', '', '2016-02-24 19:33:31', '2016-02-24 19:33:31', '', 0, 'http://localhost/wordpress/?p=9', 0, 'post', '', 0),
(10, 1, '2016-02-24 13:45:02', '2016-02-24 13:45:02', 'Este es el contenido para la información numero 1\r\n\r\n&nbsp;\r\n\r\nes solo a modo de prueba pero nos sirve para ver que tal todo\r\n\r\n&nbsp;\r\n\r\nchao!!', 'Información Número 1', '', 'inherit', 'closed', 'closed', '', '9-revision-v1', '', '', '2016-02-24 13:45:02', '2016-02-24 13:45:02', '', 9, 'http://localhost/wordpress/2016/02/24/9-revision-v1/', 0, 'revision', '', 0),
(11, 1, '2016-02-24 15:23:37', '2016-02-24 15:23:37', 'Este es el contenido para la información numero 1\r\n\r\n&nbsp;\r\n\r\nes solo a modo de prueba pero nos sirve para ver que tal todo\r\n\r\n&nbsp;\r\n\r\nchao!!', 'Información Número 1', 'solo un extracto', 'inherit', 'closed', 'closed', '', '9-revision-v1', '', '', '2016-02-24 15:23:37', '2016-02-24 15:23:37', '', 9, 'http://localhost/wordpress/2016/02/24/9-revision-v1/', 0, 'revision', '', 0),
(12, 1, '2016-02-24 16:34:07', '2016-02-24 16:34:07', '<div>SANTIAGO.- Si bien hace unas semanas dijo que no tenía pensado dejar el Bayern Munich, la nueva posibilidad de partir a la Premier League podría hacer cambiar de opinión a Arturo Vidal. Y ahora la oferta no llegaría desde el Manchester United. El Chelsea sería el nuevo interesado en contar con el volante chileno para la próxima temporada. Es que según reportan en Italia e Inglaterra, los "blues" ya tienen amarrado a su próximo entrenador, el que sería Antonio Conte, ex técnico de la Juventus. Y es por eso que el nombre de Arturo Vidal ya suena en el Chelsea, pues el DT italiano trabajó mano a mano con el volante chileno en su época dorada en la "Vecchia Signora". Incluso, la prensa italiana ya aventura que el cuadro de la Premier está siguiendo los pasos del ex Colo Colo, por expresa petición del que sería su nuevo adiestrador. Precisamente, ayer martes, para el duelo del Bayern ante la Juventus por la Champions, un grupo de veedores habría estado en el estadio italiano para ver de cerca al chileno.</div>', 'Chelsea estaría siguiendo a Arturo Vidal para ficharlo la próxima temporada', '', 'publish', 'open', 'open', '', 'solo-test-1', '', '', '2016-02-24 18:21:47', '2016-02-24 18:21:47', '', 0, 'http://localhost/wordpress/?p=12', 0, 'post', '', 0),
